var page = Forguncy.Page;
var zip_code = page.getCell("zip_code").getValue();
var address1 = page.getCell("address1");
var address2 = page.getCell("address2");
var address3 = page.getCell("address3");

if(zip_code == null){
   alert("郵便番号を入力して下さい");
   return false;
};


//住所取得開始
getAddressData(zip_code);

/* 郵便番号から都道府県・市区町村データを取得する */

function getAddressData(zip) {
  //var zipPattern = /^[0-9]{3}-?[0-9]{4}$/;
  //if(!zip.match(zipPattern)) {
  //  return false;
  //}
 
  var api = 'https://zipcloud.ibsnet.co.jp/api/search?zipcode=';
  var param = zip.replace('-', '');
  var url = api + param;
 
  fetchJsonp(url, {
    timeout: 10000,
  })
  .then((response) => {
    return response.json();
  })
  .then((data) => {
    if(data.status === 400){
      // エラー時の処理
    }else if(data.results === null){
      // 郵便番号から住所が見つからなかった場合の処理
      address1.setValue(); 
      address2.setValue(); 
      address3.setValue(); 
    } else {
      address1.setValue(data.results[0].address1);
      address2.setValue(data.results[0].address2);
      address3.setValue(data.results[0].address3);
      console.log(data.results[0]);
    }
  })

}
